    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String Cad="";
        Cad=JOptionPane.showInputDialog("Introduzca Cantidad de Elementos para Vectores:");
        int aux=Integer.parseInt(Cad);
        
        CV.SetDim(aux);
        
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        int aux=CV.GetCantidad();
        JOptionPane.showMessageDialog(rootPane,"La Cantidad de Elementos en el Vector es = "+aux);
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        if (CV.GetDim()!=-1)
            CV.CargarVector();
        else
            JOptionPane.showMessageDialog(rootPane, "Coloque la cantidad de elementos para el VECTOR");
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        //Limpiar la tabla visualmente
        this.limpiarTabla();
        
        //PARA MOSTRAR LOS ELEMENTOS DEL VECTOR
        int aux=0;
        for(int i=0;i<=CV.GetDim();i++)//utilizamos esto porque no existe en la clase y por lo tanto aqui lo generamos de nuevo
        {
            aux=CV.GetValor(i);
            jTable1.setValueAt(aux, i, 0);//muestra el VALOR,FILAS,COLUMNA en la tabla
        }
    }//GEN-LAST:event_jButton4ActionPerformed

