    private void limpiarTabla(){
        for (int i = 0; i < jTable1.getRowCount(); i++) {
            jTable1.setValueAt("", i, 0);
        }
    }
    
    private void mostrarElementosVector(){
        int aux=0;
        for(int i=0;i<=CV.GetDim();i++)//utilizamos esto porque no existe en la clase y por lo tanto aqui lo generamos de nuevo
        {
            aux=CV.GetValor(i);
            jTable1.setValueAt(aux, i, 0);//muestra el VALOR,FILAS,COLUMNA en la tabla
        }
    }
